import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [name, setName] = useState();
  const [rede, setRede] = useState();
  const [culto, setCulto] = useState();
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";
  var date = new Date();
  var dia_sem = data.getDay();
  console.log(dia_sem, "dia_sem");
  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  const post = (data) => {
    Axios.post(url, data).then((res) => {
      alert("você confirmou sua presença no culto");
    });
  };
  console.log(data, "data");
  const salve = () => {
    post({ nome: name, rede: rede, culto: culto });
  };

  return (
    <div className="container">
      <div className="header">
        <img src="/images/logo.jpg" />
        <h1>Formulário de preenchimento para o culto</h1>
      </div>
      <div className="form">
        <label>Nome :</label>
        <input
          onKeyUp={(e) => setName(e.currentTarget.value)}
          type="text"
          placeholder="Nome "
          required
        />
        <br></br>
        <label>Sobrenome:</label>
        <input
          onKeyUp={(e) => setRede(e.currentTarget.value)}
          type="text"
          placeholder="Sobrenome"
          required
        />
        <br></br>
        <label>Rede</label>
        <select
          onChange={(e) => {
            setCulto(e.target.value);
          }}
          required
        >
          <option value="">Selecione sua rede</option>
          <option value="sabadao">Pr Manoel - Ebenezér</option>
          <option value="manha">Pr Gilberto - Kadosh</option>
          <option value="noite">Pr Lincoln - Shekinah</option>
          <option value="manha">Pr Reginaldo - Radicais</option>
          <option value="noite">Pr Darliene - Kids </option>
        </select>
        <br></br>
        <label>Qual culto deseja ir?</label>
        <select
          onChange={(e) => {
            setCulto(e.target.value);
          }}
          required
        >
          <option value="">Selecione o culto</option>
          <option value="sabadao">Sábado - 19hs</option>
          <option value="manha">Domingo - 9hs</option>
          <option value="noite">Domingo - 18hs </option>
        </select>
        <br></br>
        <button onClick={salve}>Inscrever</button>
      </div>
    </div>
  );
}

export default Page;
