import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [orderListagem, setOrderListagem] = useState();
  const [listagem, setListagem] = useState("todos");
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";

  function compared(a, b) {
    if (a.nome < b.nome) return -1;
    if (a.nome > b.nome) return 1;
    return 0;
  }
  useEffect(() => {
    if (data) {
      // var array = Object.values(data);
      // const arrayList = array.sort(compared);
      setOrderListagem(data);
    }
  }, [data]);
  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  const doRemove = () => {
    remove(
      "https://aguaviva-rp.firebaseio.com/inscricoes/-MAS27YraFRA6XMwZQi2.json"
    );
  };
  const remove = (url) => {
    Axios.delete(url).then(() => {
      alert("você deletou");
      window.location = "/teste/listagem.html";
    });
  };

  return (
    <>
      <Head>
        <title>Igreja Bastista Água Viva - Ribeirão Pires</title>
      </Head>
      {Object.keys.orderListagem.map((teste) => {
        console.log(teste, "teste");
      })}
    </>
  );
}

export default Page;
