import React, { useState, useEffect } from "react";
import Logo from "../../src/components/Logo";

function Page() {
  const [clicked, setClicked] = useState();
  var url;
  if (typeof window !== "undefined") {
    url = window.location?.href;
  }
  const urlTransform = url?.split("/")[3];
  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/posts/${urlTransform}`)
      .then((response) => response.json())
      .then((json) => setClicked(json));
  }, [urlTransform]);
  return (
    <div className="container">
      <Logo />
      {clicked && (
        <div>
          <p>
            <b>#:</b> {clicked.id}
          </p>
          <p>Title:{clicked.title}</p>
          <p>Conte√∫do:{clicked.body}</p>
        </div>
      )}
    </div>
  );
}
export default Page;
