import Head from "next/head";
import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";

function Page() {
  const [registros, setRegistros] = useState();
  const [apagarRegistros, setApagarRegistros] = useState(false);
  console.log(registros);
  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((json) => setRegistros(json));
  }, []);

  function remove() {
    setApagarRegistros(true);
    fetch("https://jsonplaceholder.typicode.com/posts/1", {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((json) => setRegistros(json))
      .then((json) => setApagarRegistros(false));
  }

  function Filtro(id) {
    fetch(`https://jsonplaceholder.typicode.com/posts?userId=${id}`)
      .then((response) => response.json())
      .then((json) => setRegistros(json));
  }

  return (
    <>
      <Head>
        <title>Seja bem vindo</title>
      </Head>
      <div className="container">
        <img className="logo" src="/public/images/logo.png" alt="petz" />
        {apagarRegistros && <p>Apagando...</p>}
        <>
          <button
            className="outline-primary"
            onClick={() => {
              Filtro(1);
            }}
          >
            {" "}
            Filtrar do 1 ao 10
          </button>
          <button
            onClick={() => {
              Filtro(2);
            }}
          >
            Filtro do 11 ao 20
          </button>
          <button
            onClick={() => {
              remove();
            }}
          >
            Apagar todos os registros
          </button>
          <br></br>
          {registros?.length >= 1 && (
            <div>
              {registros.map((registro) => {
                return (
                  <>
                    <span>{registro.id}</span>

                    <span>{registro.title}</span>
                    <span>{registro.body}</span>
                    <a href={`${registro.id}`}>Visualizar</a>
                    <br />
                  </>
                );
              })}
            </div>
          )}
        </>
      </div>
    </>
  );
}

export default Page;
