import Head from "next/head";
import React, { useState, useEffect } from "react";
import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [orderListagem, setOrderListagem] = useState();
  const [listagem, setListagem] = useState("todos");
  const url = "https://aguaviva-rp.firebaseio.com/teste.json";

  function compared(a, b) {
    if (a.nome < b.nome) return -1;
    if (a.nome > b.nome) return 1;
    return 0;
  }
  useEffect(() => {
    if (data) {
      var keys = Object.keys(data);
      var values = Object.values(data);

      var y = [{ idTeste: keys, values }];
      var array = Object.entries(data);
      setOrderListagem(array);
    }
  }, [data]);
  useEffect(() => {
    setLoading(true);
    setOrderListagem;
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  var date = new Date();
  var dia_sem = date.getDay();

  const doRemove = (id) => {
    remove(`https://aguaviva-rp.firebaseio.com/teste/${id}.json`);
  };
  const remove = (url) => {
    Axios.delete(url).then(() => {
      alert(`você deletou ${url}`);
      window.location = "listagem";
    });
  };

  const doEdit = (id, data) => {
    if (data.chegada) {
      edit(`https://aguaviva-rp.firebaseio.com/teste/${id}.json`, {
        ...data,
        chegada: false,
      });
    } else {
      edit(`https://aguaviva-rp.firebaseio.com/teste/${id}.json`, {
        ...data,
        chegada: true,
      });
    }
  };
  const edit = (url, data) => {
    Axios.put(url, data).then(() => {
      alert(`você editou ${url}`);
      window.location = "listagem";
    });
  };
  return (
    <>
      <Head>
        <title>Igreja Batista Água Viva - Ribeirão Pires</title>
      </Head>
      <div className="container">
        <div className="header">
          <img src="/images/logo.jpg" />
          <h1>Listagem de controle de presença no culto</h1>
        </div>
        {data && (
          <>
            <div className="select">
              <label>Selecione a listagem do culto que deseja ver</label>
              <select
                onChange={(e) => {
                  setListagem(e.target.value);
                }}
              >
                <option value="todos">Todos</option>
                <option value="sabado">Sabado - 19hs</option>
                <option value="manha">Domingo - 9hs</option>
                <option value="noite">Domingo - 18hs</option>
              </select>
            </div>
            {listagem == "sabado" && (
              <>
                <h1>Sabado - 19hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          return (
                            <>
                              {individuo.map((ind) => {
                                if (ind.culto == "sabado") {
                                  return (
                                    <tr key={ind} className={individuo[0]}>
                                      <td>
                                        {ind.nome} {ind.sobrenome}
                                      </td>
                                      <td>Pr {ind.rede}</td>
                                      <td>
                                        <button
                                          className="remove"
                                          onClick={(e) =>
                                            doRemove(individuo[0])
                                          }
                                        >
                                          Remover
                                        </button>
                                      </td>
                                    </tr>
                                  );
                                }
                              })}
                            </>
                          );
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
            {listagem == "manha" && (
              <>
                <h1>Domingo - 9hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          return (
                            <>
                              {individuo.map((ind) => {
                                if (ind.culto == "manha") {
                                  return (
                                    <tr key={ind} className={individuo[0]}>
                                      <td>
                                        {ind.nome} {ind.sobrenome}
                                      </td>
                                      <td>Pr {ind.rede}</td>
                                      <td>
                                        <button
                                          className="remove"
                                          onClick={(e) =>
                                            doRemove(individuo[0])
                                          }
                                        >
                                          Remover
                                        </button>
                                      </td>
                                    </tr>
                                  );
                                }
                              })}
                            </>
                          );
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
            {listagem == "noite" && (
              <>
                <h1>Domingo - 18hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {Object.entries(orderListagem).map((individuo) => {
                          return (
                            <>
                              {individuo.map((ind) => {
                                if (ind.culto == "noite") {
                                  return (
                                    <tr key={ind} className={individuo[0]}>
                                      <td>
                                        {ind.nome} {ind.sobrenome}
                                      </td>
                                      <td>Pr {ind.rede}</td>
                                      <td>
                                        <button
                                          className="remove"
                                          onClick={(e) =>
                                            doRemove(individuo[0])
                                          }
                                        >
                                          Remover
                                        </button>
                                      </td>
                                    </tr>
                                  );
                                }
                              })}
                            </>
                          );
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
            {listagem == "todos" && (
              <>
                <h1>Sabado - 19hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          return (
                            <>
                              {individuo.map((ind) => {
                                if (ind.culto == "sabado") {
                                  return (
                                    <tr key={ind} className={individuo[0]}>
                                      <td>
                                        {ind.nome} {ind.sobrenome}
                                      </td>
                                      <td>Pr {ind.rede}</td>
                                      <td>
                                        <button
                                          className="remove"
                                          onClick={(e) =>
                                            doRemove(individuo[0])
                                          }
                                        >
                                          Remover
                                        </button>
                                      </td>
                                    </tr>
                                  );
                                }
                              })}
                            </>
                          );
                        })}
                      </>
                    )}
                  </tbody>
                </table>

                <h1>Domingo - 9hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                      <th>Chegada</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          return (
                            <>
                              {individuo.map((ind) => {
                                if (ind.culto == "manha") {
                                  return (
                                    <tr key={ind} className={individuo[0]}>
                                      <td>
                                        {ind.nome} {ind.sobrenome}
                                      </td>
                                      <td>Pr {ind.rede}</td>
                                      <td>
                                        <button
                                          className="remove"
                                          onClick={(e) =>
                                            doRemove(individuo[0])
                                          }
                                        >
                                          Remover
                                        </button>
                                      </td>
                                      <td
                                        onClick={(e) =>
                                          doEdit(individuo[0], ind)
                                        }
                                      >
                                        {ind.chegada ? "chegou" : "não chegou"}
                                      </td>
                                    </tr>
                                  );
                                }
                              })}
                            </>
                          );
                        })}
                      </>
                    )}
                  </tbody>
                </table>

                <h1>Domingo - 18hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          return (
                            <>
                              {individuo.map((ind) => {
                                if (ind.culto == "noite") {
                                  return (
                                    <tr key={ind} className={individuo[0]}>
                                      <td>
                                        {ind.nome} {ind.sobrenome}
                                      </td>
                                      <td>Pr {ind.rede}</td>
                                      <td>
                                        <button
                                          onClick={(e) =>
                                            doRemove(individuo[0])
                                          }
                                          className="remove"
                                        >
                                          Remover
                                        </button>
                                      </td>
                                    </tr>
                                  );
                                }
                              })}
                            </>
                          );
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
          </>
        )}
        {loading && <p>Carregando...</p>}
        <div className="box-voltar">
          <a href="/" className="button">
            Voltar
          </a>
        </div>
      </div>
    </>
  );
}

export default Page;
