import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [name, setName] = useState();
  const [rede, setRede] = useState();
  const [culto, setCulto] = useState();
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";

  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  const post = (data) => {
    Axios.post(url, data).then((res) => {
      alert("Você confirmou sua presença no culto");
    });
  };
  console.log(data, "data");
  const salve = () => {
    post({ nome: name, rede: rede, culto: culto });
  };

  return (
    <>
      {data && (
        <>
          <h1>Manhã</h1>
          <table>
            <thead>
              <tr>
                <th>Nome</th>
                <th>Culto</th>
                <th>Rede</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(data).map((individuo) => {
                if (data[individuo].culto == "manha") {
                  return (
                    <tr key={individuo}>
                      <td>
                        {data[individuo].nome} {data[individuo].sobrenome}
                      </td>
                      <td>{data[individuo].rede}</td>
                    </tr>
                  );
                }
              })}
            </tbody>
          </table>
          <h1>Noite</h1>
          <table>
            <thead>
              <tr>
                <th>Nome</th>
                <th>Rede</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(data).map((individuo) => {
                if (data[individuo].culto == "noite") {
                  return (
                    <tr key={individuo}>
                      <td>
                        {data[individuo].nome} {data[individuo].sobrenome}
                      </td>
                      <td>{data[individuo].rede}</td>
                    </tr>
                  );
                }
              })}
            </tbody>
          </table>
        </>
      )}
      {loading && <p>Loading</p>}
    </>
  );
}

export default Page;
