import React from "react";

export default function TableRecords({ registros, filtrar, apagarRegistros }) {
  return (
    <>
      {apagarRegistros && <div>Os registros foram apagados</div>}
      {registros?.length >= 1 && !apagarRegistros && (
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Titulo</th>
              <th>Detalhes</th>
            </tr>
          </thead>

          {filtrar?.length == 0 && (
            <tbody>
              {registros.map((registro) => {
                return (
                  <tr>
                    <td>{registro.id}</td>
                    <td>{registro.title}</td>
                    <td>
                      {" "}
                      <a href={`${registro.id}`}>Visualizar</a>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          )}
          {filtrar?.length > 0 && (
            <tbody>
              {filtrar.map((registro) => {
                return (
                  <tr>
                    <td>{registro.id}</td>
                    <td>{registro.title}</td>
                    <td>
                      {" "}
                      <a className="visualizar" href={`${registro.id}`}>
                        Visualizar
                      </a>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          )}
        </table>
      )}
    </>
  );
}
