import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [name, setName] = useState();
  const [rede, setRede] = useState();
  const [culto, setCulto] = useState();
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";

  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  const post = (data) => {
    Axios.post(url, data).then((res) => {
      alert("você confirmou sua presença no culto");
    });
  };
  console.log(data, "data");
  const salve = () => {
    post({ nome: name, rede: rede, culto: culto });
  };

  return (
    <div className="container">
      <div className="header">
        <img src="/images/logo.jpg" />
        <h1>Formulário de preenchimento para o culto</h1>
      </div>
      <div className="form">
        <label>Nome Completo:</label>
        <input
          onKeyUp={(e) => setName(e.currentTarget.value)}
          type="text"
          placeholder="Nome Completo"
          required
        />
        <br></br>
        <label>Rede:</label>
        <input
          onKeyUp={(e) => setRede(e.currentTarget.value)}
          type="text"
          placeholder="Rede"
          required
        />
        <br></br>
        <label>Qual culto deseja ir?</label>
        <select
          onChange={(e) => {
            setCulto(e.target.value);
          }}
          required
        >
          <option value="">Selecione o Culto</option>
          <option value="manha">Domingo de manhã - 9hs</option>
          <option value="noite">Domingo de noite - 18hs </option>
        </select>
        <br></br>
        <button onClick={salve}>Inscrever</button>
      </div>
    </div>
  );
}

export default Page;
