import React from "react";
import Form from "react-bootstrap/Form";

export default function Header({ apagarRegistros, filtrarRegistros }) {
  <header className="header">
    <img className="logo" src="/images/logo.png" alt="petz" />
    {apagarRegistros && <p>Apagando...</p>}
    {filtrarRegistros && <p>Filtrando...</p>}
    <div className="filtros">
      <Form.Group className="select">
        <Form.Label>Filtrar:</Form.Label>
        <Form.Control
          as="select"
          value="Choose..."
          onChange={(e) => {
            filtrar(e.target.value);
          }}
        >
          <option>Selecione</option>
          <option value="1"> 1 ao 10</option>
          <option value="2"> 11 ao 20</option>
        </Form.Control>
      </Form.Group>

      <Button
        onClick={() => {
          remove();
        }}
      >
        Apagar todos os registros
      </Button>
    </div>
  </header>;
}
