import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [name, setName] = useState();
  const [rede, setRede] = useState();
  const [culto, setCulto] = useState();
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";

  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  const post = (data) => {
    Axios.post(url, data).then((res) => {});
  };
  console.log(data, "data");
  const salve = () => {
    post({ nome: name, rede: rede, culto: culto });
  };

  return (
    <>
      <label>Nome Completo:</label>
      <input
        onKeyUp={(e) => setName(e.currentTarget.value)}
        type="text"
        placeholder="Pesquisar por estudante"
        required
      />
      <br></br>
      <label>Rede:</label>
      <input
        onKeyUp={(e) => setRede(e.currentTarget.value)}
        type="text"
        placeholder="Pesquisar por estudante"
        required
      />
      <br></br>
      <label>Qual culto deseja ir?</label>
      <select
        onChange={(e) => {
          setCulto(e.target.value);
        }}
        required
      >
        <option value="">Selecione o Culto</option>
        <option value="manha">Manha</option>
        <option value="noite">Noite</option>
      </select>
      <br></br>
      <button onClick={salve}>Salvar</button>

      {data && (
        <table>
          <thead>
            <tr>
              <th>Nome</th>
              <th>Culto</th>
              <th>Rede</th>
            </tr>
          </thead>
          <tbody>
            {Object.keys(data).map((individuo) => {
              if (data[individuo].culto == manha) {
                return (
                  <tr key={individuo}>
                    <td>{data[individuo].nome}</td>
                    <td>{data[individuo].culto}</td>
                    <td>{data[individuo].rede}</td>
                  </tr>
                );
              }
            })}
          </tbody>
        </table>
      )}
      {loading && <p>Loading</p>}
    </>
  );
}

export default Page;
