import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [orderListagem, setOrderListagem] = useState();
  const [listagem, setListagem] = useState("todos");
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";

  // var array = ["jose", "maria", "anas"];
  function compared(a, b) {
    if (a.nome < b.nome) return -1;
    if (a.nome > b.nome) return 1;
    return 0;
  }
  // // // const g = x.sort(compared);
  // const j = [data].sort(compared);
  useEffect(() => {
    if (data) {
      var array = Object.values(data);
      const arrayList = array.sort(compared);
      setOrderListagem(arrayList);
    }
  }, [data]);

  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  return (
    <>
      {data && (
        <>
          <label>Selecione a listagem do culto que deseja ver</label>
          <select
            onChange={(e) => {
              setListagem(e.target.value);
            }}
          >
            <option value="todos">Todos</option>
            <option value="sabado">Sabado - 19hs</option>
            <option value="manha">Domingo - 9hs</option>
            <option value="noite">Domingo - 18hs</option>
          </select>
          {(listagem == "sabado" || listagem == "todos") && (
            <>
              <h1>Sabado</h1>
              <table>
                <thead>
                  <tr>
                    <th>Nome</th>
                    <th>Rede</th>
                  </tr>
                </thead>
                <tbody>
                  {orderListagem && (
                    <>
                      {orderListagem.map((individuo) => {
                        if (individuo.culto == "sabado") {
                          return (
                            <tr key={individuo}>
                              <td>
                                {individuo.nome} {individuo.sobrenome}
                              </td>
                              <td>Pr {individuo.rede}</td>
                            </tr>
                          );
                        }
                      })}
                    </>
                  )}
                </tbody>
              </table>
            </>
          )}
          {(listagem == "manha" || listagem == "todos") && (
            <>
              <h1>Manh√£</h1>
              <table>
                <thead>
                  <tr>
                    <th>Nome</th>
                    <th>Rede</th>
                  </tr>
                </thead>
                <tbody>
                  {orderListagem && (
                    <>
                      {orderListagem.map((individuo) => {
                        if (individuo.culto == "manha") {
                          return (
                            <tr key={individuo}>
                              <td>
                                {individuo.nome} {individuo.sobrenome}
                              </td>
                              <td>{individuo.rede}</td>
                            </tr>
                          );
                        }
                      })}
                    </>
                  )}
                </tbody>
              </table>
            </>
          )}
          {(listagem == "noite" || listagem == "todos") && (
            <>
              <h1>Noite</h1>
              <table>
                <thead>
                  <tr>
                    <th>Nome</th>
                    <th>Rede</th>
                  </tr>
                </thead>
                <tbody>
                  {orderListagem && (
                    <>
                      {orderListagem.map((individuo) => {
                        if (individuo.culto == "noite") {
                          return (
                            <tr key={individuo}>
                              <td>
                                {individuo.nome} {individuo.sobrenome}
                              </td>
                              <td>{individuo.rede}</td>
                            </tr>
                          );
                        }
                      })}
                    </>
                  )}
                </tbody>
              </table>
            </>
          )}
        </>
      )}
      {loading && <p>Loading</p>}
    </>
  );
}

export default Page;
