import Head from "next/head";
import React, { useState, useEffect } from "react";
import Header from "../src/components/Header";
import TableRecords from "../src/components/TableRecords";
import { Posts } from "../src/services/requests";

function Page({ registros }) {
  const [filtrarRegistros, setFiltraRegistros] = useState(false);
  return (
    <>
      <Head>
        <title>Seja bem vindo</title>
      </Head>
      <div className="container">
        <Header
          setFiltraRegistros={setFiltraRegistros}
          filtrarRegistros={filtrarRegistros}
        />
        <TableRecords registros={registros} />
        {!registros && <div>Todos os registros foram apagados</div>}
      </div>
    </>
  );
}

Page.getInitialProps = async ({ query, filtrarRegistros }) => {
  const registros = await Posts();
  // const remover = await Posts();

  const filtrar = await Filtrar(filtrarRegistros);

  return {
    registros,
  };
};

export default Page;
