import Head from "next/head";
import React, { useState, useEffect } from "react";
import Header from "../src/components/Header";
import TableRecords from "../src/components/TableRecords";
import { Posts, Filtrar, Remover } from "../src/services/requests";
import { updateParameter } from "../src/helpers/update";

import Logo from "../src/components/Logo";

function Page({ registros, filtrar }) {
  const [apagarRegistros, setApagarRegistros] = useState(false);

  async function Deletar(res, req) {
    const request = await fetch("/api/delete", {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((req) => setApagarRegistros(req))
      .catch((err) => {
        return { error: err };
      });

    return "/";
  }

  return (
    <>
      <Head>
        <title>Seja bem vindo</title>
      </Head>
      <div className="container">
        <header className="header">
          <Logo />
          <div className="loading">
            {apagarRegistros && <span>Apagando...</span>}
            {filtrar && <span>Filtrando...</span>}
          </div>
          <div className="filtros">
            <form className="select">
              <label>Filtrar:</label>
              <select
                as="select"
                value="Choose..."
                onChange={(e) => {
                  window.location = updateParameter(
                    window.location.href,
                    "subject",
                    e.target.value
                  );
                }}
              >
                <option>Selecione</option>
                <option value="1"> 1 ao 10</option>
                <option value="2"> 11 ao 20</option>
                <option value="3"> 21 ao 30</option>
                <option value="4"> 31 ao 40</option>
                <option value="5"> 41 ao 50</option>
                <option value="6"> 51 ao 60</option>
                <option value="7"> 61 ao 70</option>
                <option value="8"> 71 ao 80</option>
                <option value="9"> 81 ao 90</option>
                <option value="10"> 91 ao 100</option>
              </select>
            </form>
            <button
              onClick={() => {
                Deletar();
              }}
            >
              Apagar todos os registros
            </button>
          </div>
        </header>
        <TableRecords registros={registros} filtrar={filtrar} />
        {!registros && <div>Todos os registros foram apagados</div>}
      </div>
    </>
  );
}

Page.getInitialProps = async ({ query }) => {
  const filtrar = await Filtrar(query.subject);
  const registros = await Posts();
  return {
    registros,
    filtrar,
  };
};

export default Page;
