import React from "react";
import Form from "react-bootstrap/Form";
export default function Filters({
  Button,
  setApagarRegistros,
  setFiltraRegistros,
  setRegistros,
}) {
  function remove() {
    setApagarRegistros(true);
    fetch("https://jsonplaceholder.typicode.com/posts/1", {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((json) => setRegistros(json))
      .then((json) => setApagarRegistros(false));
  }

  function filtrar(value) {
    if (value >= 1) {
      setFiltraRegistros(true);
      fetch(`https://jsonplaceholder.typicode.com/posts?userId=${value}`)
        .then((response) => response.json())
        .then((json) => setRegistros(json))
        .then((json) => setFiltraRegistros(json));
    }
  }
  return (
    <div className="filtros">
      <Form.Group className="select">
        <Form.Label>Filtrar:</Form.Label>
        <Form.Control
          as="select"
          value="Choose..."
          onChange={(e) => {
            filtrar(e.target.value);
          }}
        >
          <option>Selecione</option>
          <option value="1"> 1 ao 10</option>
          <option value="2"> 11 ao 20</option>
          <option value="3"> 21 ao 30</option>
          <option value="4"> 31 ao 40</option>
          <option value="5"> 41 ao 50</option>
          <option value="6"> 51 ao 60</option>
          <option value="7"> 61 ao 70</option>
          <option value="8"> 71 ao 80</option>
          <option value="9"> 81 ao 90</option>
          <option value="10"> 91 ao 100</option>
        </Form.Control>
      </Form.Group>
      <Button
        onClick={() => {
          remove();
        }}
      >
        Apagar todos os registros
      </Button>
    </div>
  );
}
