import React, { useState, useEffect } from "react";
import Logo from "../../src/components/Logo";

function Page() {
  const [clicked, setClicked] = useState();
  var url;
  if (typeof window !== "undefined") {
    url = window.location?.href;
  }
  const urlTransform = url?.split("/")[3];
  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/posts/${urlTransform}`)
      .then((response) => response.json())
      .then((json) => setClicked(json));
  }, [urlTransform]);
  return (
    <div className="container">
      <Logo />
      <h2 className="subtitle">Detalhes</h2>
      <div>
        {clicked && (
          <>
            <p>
              <b>#: </b> {clicked.id}
            </p>
            <p>
              <b>Title: </b>
              {clicked.title}
            </p>
            <p>
              <b>Conte√∫do: </b>
              {clicked.body}
            </p>
          </>
        )}
      </div>
      <a className="voltar" href="/">
        Voltar
      </a>
    </div>
  );
}
Page.getInitialProps = async ({ query }) => {
  const filtrar = await Filtrar(query.subject);
  const registros = await Posts();
  return {
    registros,
    filtrar,
    query,
  };
};

export default Page;
