import Head from "next/head";
import React, { useState, useEffect } from "react";
import TableRecords from "../src/components/TableRecords";
import { Posts, Filtrar } from "../src/services/requests";
import { updateParameter } from "../src/helpers/update";

import Logo from "../src/components/Logo";

function Page({ registros, filtrar, query }) {
  const [apagarRegistros, setApagarRegistros] = useState(false);
  const [filtrando, setFiltrando] = useState(false);
  const [loading, setLoading] = useState(false);
  async function Deletar() {
    await fetch("http://jsonplaceholder.typicode.com/posts/1", {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((req) => setApagarRegistros(req))
      .catch((err) => {
        return { error: err };
      });

    return setLoading(false);
  }

  return (
    <>
      <Head>
        <title>Seja bem vindo</title>
      </Head>
      <div className="container">
        <header className="header">
          <Logo />
          {loading && (
            <div className="loading">
              {" "}
              <img src="/images/loading.gif" alt="loading" />
              Apagando...
            </div>
          )}
          {filtrando && (
            <div className="loading">
              {" "}
              <img src="/images/loading.gif" alt="loading" />
              Filtrando...
            </div>
          )}

          <div className="filtros">
            <form className="select">
              <label>Filtrar:</label>
              <select
                as="select"
                defaultValue={query.filtro}
                onChange={(e) => {
                  setFiltrando(true);
                  window.location = updateParameter(
                    window.location.href,
                    "filtro",
                    e.target.value
                  );
                }}
              >
                <option>Selecione</option>
                <option value="1"> 1 ao 10</option>
                <option value="2"> 11 ao 20</option>
                <option value="3"> 21 ao 30</option>
                <option value="4"> 31 ao 40</option>
                <option value="5"> 41 ao 50</option>
                <option value="6"> 51 ao 60</option>
                <option value="7"> 61 ao 70</option>
                <option value="8"> 71 ao 80</option>
                <option value="9"> 81 ao 90</option>
                <option value="10"> 91 ao 100</option>
              </select>
            </form>
            <button
              className="remover"
              onClick={() => {
                Deletar();
                setLoading(true);
              }}
            >
              Apagar todos os registros
            </button>
          </div>
        </header>
        <TableRecords
          registros={registros}
          filtrar={filtrar}
          apagarRegistros={apagarRegistros}
          setFiltrando={setFiltrando}
        />
        {!registros && (
          <div className="detalhes">
            <p>Todos os registros foram apagados</p>
          </div>
        )}
      </div>
    </>
  );
}

Page.getInitialProps = async ({ query }) => {
  let filtrar = [];
  if (query.filtro) {
    filtrar = await Filtrar(query.filtro);
  }
  const registros = await Posts();
  return {
    registros,
    filtrar,
    query,
  };
};

export default Page;
