import request from "./request";

export function Posts() {
  return request(`https://jsonplaceholder.typicode.com/posts`)
    .then((res) => res.json())
    .catch((err) => {
      throw err;
    });
}
export function Filtrar(subject) {
  return request(`https://jsonplaceholder.typicode.com/posts?userId=${subject}`)
    .then((res) => res.json())
    .catch((err) => {
      throw err;
    });
}
export function Remover() {
  console.log("chegou");
  return request(`https://jsonplaceholder.typicode.com/posts/1`, {
    method: "DELETE",
    headers: {
      "Content-Type": "application/json",
    },
  })
    .then((res) => res.json())
    .catch((err) => {
      throw err;
    });
}
