import React, { useState } from "react";

import Logo from "./Logo";
import Filters from "./Filters";
import Spinner from "react-bootstrap/Spinner";

export default function Header({ Button, setRegistros }) {
  const [apagarRegistros, setApagarRegistros] = useState(false);
  const [filtrarRegistros, setFiltraRegistros] = useState(false);

  return (
    <header className="header">
      <Logo />
      <div className="loading">
        {apagarRegistros && (
          <>
            <Spinner animation="border" role="status"></Spinner>
            <span>Apagando...</span>
          </>
        )}
        {filtrarRegistros && (
          <>
            <Spinner animation="border" role="status"></Spinner>
            <span>Filtrando...</span>
          </>
        )}
      </div>
      <Filters
        Button={Button}
        setApagarRegistros={setApagarRegistros}
        setFiltraRegistros={setFiltraRegistros}
        setRegistros={setRegistros}
      />
    </header>
  );
}
