import Head from "next/head";
import React, { useState, useEffect } from "react";
import ReactGA from "react-ga";
import Axios from "axios";

function Page() {
  function initialGA() {
    ReactGA.initialize("UA-170909874-1");
    ReactGA.pageview(urlLocal);
  }
  let urlLocal;
  if (typeof window !== "undefined") {
    urlLocal = window.location?.href;
  }
  initialGA();
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [orderListagem, setOrderListagem] = useState();
  const [listagem, setListagem] = useState("todos");
  const url = "https://aguaviva-rp.firebaseio.com/teste.json";
  var date = new Date();
  var dia_sem = date.getDay();
  function compared(a, b) {
    if (a.nome < b.nome) return -1;
    if (a.nome > b.nome) return 1;
    return 0;
  }

  useEffect(() => {
    if (data) {
      var array = Object.values(data);
      const arrayList = array.sort(compared);
      setOrderListagem(arrayList);
    }
  }, [data]);

  useEffect(() => {
    // if (data) {
    //   if (dia_sem >= 1 && dia_sem <= 3) {
    //     Object.entries(data).map((individuo) => {
    //       if (individuo[0] != "base") {
    //         doRemove(individuo[0]);
    //       }
    //     });
    //   }
    // }
  }, [data]);
  const doRemove = (id) => {
    remove(`https://aguaviva-rp.firebaseio.com/teste/${id}.json`);
  };
  const remove = (url) => {
    Axios.delete(url).then(() => {
      console.log(`você deletou ${url}`);
    });
  };
  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  const doEdit = (id) => {
    edit(`https://aguaviva-rp.firebaseio.com/teste/${id}.json`);
  };
  const edit = (url) => {
    Axios.put(url).then(() => {
      console.log(`você deletou ${url}`);
    });
  };

  const post = (data) => {
    Axios.post(url, data).then((res) => {
      setNomeLimpar(!nomeLimpar);
      setSobrenomeLimpar(!sobrenomeLimpar);
      setRedeLimpar(!redeLimpar);
      alert("você confirmou sua presença no culto");
      window.location = "/listagem.html";
    });
  };

  const salve = () => {
    if (nome && sobrenome && rede && culto) {
      put({
        status: true,
      });
    } else {
      alert("Um ou mais campos estão em branco");
    }
  };

  return (
    <>
      <Head>
        <title>Igreja Batista Água Viva - Ribeirão Pires</title>
      </Head>
      <div className="container">
        <div className="header">
          <img src="/images/logo.jpg" />
          <h1>Listagem de controle de presença no culto</h1>
        </div>
        {data && (
          <>
            <div className="select">
              <label>Selecione a listagem do culto que deseja ver</label>
              <select
                onChange={(e) => {
                  setListagem(e.target.value);
                }}
              >
                <option value="todos">Todos</option>
                <option value="sabado">Sabado - 19hs</option>
                <option value="manha">Domingo - 9hs</option>
                <option value="noite">Domingo - 18hs</option>
              </select>
            </div>
            {listagem == "sabado" && (
              <>
                <h1>Sabado - 19hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "sabado") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
            {listagem == "manha" && (
              <>
                <h1>Domingo - 9hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "manha") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
            {listagem == "noite" && (
              <>
                <h1>Domingo - 18hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "noite") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
            {listagem == "todos" && (
              <>
                <h1>Sabado - 19hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "sabado") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>

                <h1>Domingo - 9hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "manha") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                                <td>Click</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>

                <h1>Domingo - 18hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "noite") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
            <div className="box-voltar">
              <a href="/" className="button">
                Voltar
              </a>
            </div>
          </>
        )}
        {loading && (
          <p className="carregando">
            <img src="/images/loading.gif" />
            <span>Carregando...</span>
          </p>
        )}
      </div>
    </>
  );
}

export default Page;
