import Head from "next/head";
import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";
import Header from "../src/components/Header";
import TableRecords from "../src/components/TableRecords";

function Page() {
  const [registros, setRegistros] = useState();
  const [apagarRegistros, setApagarRegistros] = useState(false);
  const [filtrarRegistros, setFiltraRegistros] = useState(false);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((json) => setRegistros(json));
  }, []);

  function remove() {
    setApagarRegistros(true);
    fetch("https://jsonplaceholder.typicode.com/posts/1", {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((json) => setRegistros(json))
      .then((json) => setApagarRegistros(false));
  }

  function filtrar(value) {
    if (value >= 1) {
      setFiltraRegistros(true);
      fetch(`https://jsonplaceholder.typicode.com/posts?userId=${value}`)
        .then((response) => response.json())
        .then((json) => setRegistros(json))
        .then((json) => setFiltraRegistros(json));
    }
  }

  return (
    <>
      <Head>
        <title>Seja bem vindo</title>
      </Head>
      <div className="container">
        <Header
          apagarRegistros={apagarRegistros}
          filtrarRegistros={filtrarRegistros}
          Button={Button}
        />
        <TableRecords registros={registros} Table={Table} Button={Button} />
        {!registros && <div>Todos os registros foram apagados</div>}
      </div>
    </>
  );
}

export default Page;
