import Head from "next/head";
import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";
import Header from "../src/components/Header";
import TableRecords from "../src/components/TableRecords";

function Page() {
  const [registros, setRegistros] = useState();
  // useEffect(() => {
  //   fetch("https://jsonplaceholder.typicode.com/posts")
  //     .then((response) => response.json())
  //     .then((json) => setRegistros(json));
  // }, []);
  return (
    <>
      <Head>
        <title>Seja bem vindo</title>
      </Head>
      <div className="container">
        <Header Button={Button} setRegistros={setRegistros} />
        <TableRecords registros={registros} Table={Table} Button={Button} />
        {!registros && <div>Todos os registros foram apagados</div>}
      </div>
    </>
  );
}

Page.getInitialProps = async ({ query }) => {
  const gradeyear = await listGradeGroups(2);

  return {
    gradeyear,
  };
};

export default Page;
