import Head from "next/head";
import React, { useState, useEffect } from "react";

function Page() {
  const [registros, setRegistros] = useState();
  const [ei, setEi] = useState(false);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((json) => setRegistros(json));
  }, []);

  function remove() {
    setEi(true);
    console.log(ei);
    fetch("https://jsonplaceholder.typicode.com/posts/1", {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((json) => setRegistros(json));
    setEi(false);
  }

  function Filtro(id) {
    fetch(`https://jsonplaceholder.typicode.com/posts?userId=${id}`)
      .then((response) => response.json())
      .then((json) => setRegistros(json));
  }

  return (
    <>
      <Head>
        <title>Seja bem vindo</title>
      </Head>
      <div>
        {ei && <p>loading</p>}
        <>
          <button
            onClick={() => {
              Filtro(1);
            }}
          >
            Filtro 1
          </button>
          <button
            onClick={() => {
              Filtro(2);
            }}
          >
            Filtro 2
          </button>
          <button
            onClick={() => {
              remove();
            }}
          >
            Apagar todos os registros sdsd
          </button>
          <br></br>
          {registros?.length >= 1 && (
            <div>
              {registros.map((registro) => {
                return (
                  <>
                    <span>{registro.id}</span>
                    <a href={`${registro.id}`}>Visualizar</a>
                    <br />
                  </>
                );
              })}
            </div>
          )}
        </>
      </div>
    </>
  );
}

export default Page;
