import request from "./request";

export function Posts() {
  return request(`https://jsonplaceholder.typicode.com/posts`)
    .then((res) => res.json())
    .catch((err) => {
      throw err;
    });
}
export function Filtrar(value) {
  return request(`https://jsonplaceholder.typicode.com/posts?userId=${value}`)
    .then((res) => res.json())
    .catch((err) => {
      throw err;
    });
}
export function Remover() {
  return request(`https://jsonplaceholder.typicode.com/posts/1`)
    .then((res) => res.json())
    .catch((err) => {
      throw err;
    });
}
