import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";

  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  const post = (data) => {
    Axios.post(url, data).then((res) => {
      console.log(res.data);
    });
  };

  const salve = () => {
    post({ nome: "Felipe Paulino", rede: "radicais", culto: "noite" });
  };

  return (
    <>
      <div>teste</div>
      {JSON.stringify(data)}

      <button onClick={salve}>Salvar</button>
      {loading && <p>Loading</p>}
    </>
  );
}

export default Page;
