import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();

  const [listagem, setListagem] = useState();

  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";

  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  return (
    <>
      {data && (
        <>
          <label>Selecione a listagem do culto que deseja ver</label>
          <select
            onChange={(e) => {
              setListagem(e.target.value);
            }}
          >
            <option value="todos">Todos</option>
            <option value="sabado">Sabado - 19hs</option>
            <option value="manha">Domingo - 9hs</option>
            <option value="noite">Domingo - 18hs</option>
          </select>
          {listagem == "sabado" && (
            <>
              <h1>Sabado</h1>
              <table>
                <thead>
                  <tr>
                    <th>Nome</th>
                    <th>Rede</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.keys(data).map((individuo) => {
                    if (data[individuo].culto == "sabado") {
                      return (
                        <tr key={individuo}>
                          <td>
                            {data[individuo].nome} {data[individuo].sobrenome}
                          </td>
                          <td>Pr {data[individuo].rede}</td>
                        </tr>
                      );
                    }
                  })}
                </tbody>
              </table>
            </>
          )}
          <h1>Manh√£</h1>
          <table>
            <thead>
              <tr>
                <th>Nome</th>
                <th>Rede</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(data).map((individuo) => {
                if (data[individuo].culto == "manha") {
                  return (
                    <tr key={individuo}>
                      <td>
                        {data[individuo].nome} {data[individuo].sobrenome}
                      </td>
                      <td>{data[individuo].rede}</td>
                    </tr>
                  );
                }
              })}
            </tbody>
          </table>
          <h1>Noite</h1>
          <table>
            <thead>
              <tr>
                <th>Nome</th>
                <th>Rede</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(data).map((individuo) => {
                if (data[individuo].culto == "noite") {
                  return (
                    <tr key={individuo}>
                      <td>
                        {data[individuo].nome} {data[individuo].sobrenome}
                      </td>
                      <td>{data[individuo].rede}</td>
                    </tr>
                  );
                }
              })}
            </tbody>
          </table>
        </>
      )}
      {loading && <p>Loading</p>}
    </>
  );
}

export default Page;
