import React, { useState, useEffect } from "react";

import { updateParameter } from "../src/helpers/update";
import {
  MakeCar,
  ModelMake,
  VersionModel,
  VehiclesPage1,
  VehiclesPage2,
  VehiclesPage3,
} from "../src/services/requests";

function Page({
  GetMakeCar,
  GetVehiclesPage1,
  GetVehiclesPage2,
  GetVehiclesPage3,
}) {
  const [modelId, setModelId] = useState();
  const [model, listModel] = useState();
  const [versionId, setVersionId] = useState();
  const [version, listVersion] = useState();
  const readModel = async () => {
    const GetModelMake = await ModelMake(modelId);
    listModel(GetModelMake);
  };
  useEffect(() => {
    readModel();
  }, [modelId]);

  const readVersion = async () => {
    const GetVersionModel = await VersionModel(versionId);
    listVersion(GetVersionModel);
  };
  useEffect(() => {
    readVersion();
  }, [versionId]);

  const setAllVehicles = () => {
    {
      [...GetVehiclesPage1], [...GetVehiclesPage2], [...GetVehiclesPage3];
    }
  };

  console.log(setAllVehicles, "setAllVehicles");
  return (
    <>
      <form>
        <input name="locale" placeholder="Onde:"></input>
        <select className="input" name="distance">
          <option placeholder="Raio:"></option>
        </select>

        <select
          className="input"
          name="make"
          onChange={(e) => {
            setModelId(e.target.value);
          }}
        >
          <option>Todas</option>
          {GetMakeCar.map((make) => {
            return (
              <option value={make.ID} key={make.ID}>
                {make.Name}
              </option>
            );
          })}
        </select>
        <select
          className="input"
          name="model"
          onChange={(e) => {
            setVersionId(e.target.value);
          }}
        >
          {model && (
            <>
              <option>Todas</option>
              {model.map((mode) => {
                return (
                  <option value={mode.ID} key={mode.ID}>
                    {mode.Name}
                  </option>
                );
              })}
            </>
          )}
        </select>
        <select className="input" name="year">
          <option></option>
        </select>
        <select className="input" name="price">
          <option></option>
        </select>
        <select className="input" name="version">
          {version && (
            <>
              <option>Todas</option>
              {version.map((versio) => {
                return (
                  <option value={versio.ID} key={versio.ID}>
                    {versio.Name}
                  </option>
                );
              })}
            </>
          )}
        </select>
      </form>
    </>
  );
}

Page.getInitialProps = async () => {
  const GetMakeCar = await MakeCar();
  const GetVehiclesPage1 = await VehiclesPage1();
  const GetVehiclesPage2 = await VehiclesPage2();
  const GetVehiclesPage3 = await VehiclesPage3();
  return {
    GetMakeCar,
    GetVehiclesPage1,
    GetVehiclesPage2,
    GetVehiclesPage3,
  };
};

export default Page;
