import React, { useState, useEffect } from "react";

import { updateParameter } from "../src/helpers/update";
import {
  MakeCar,
  ModelMake,
  VersionModel,
  Vehicles,
} from "../src/services/requests";

function Page({ GetMakeCar }) {
  const [modelId, setModelId] = useState();
  const [model, listModel] = useState();
  const [versionId, setVersionId] = useState();
  const [version, listVersion] = useState();
  const readModel = async () => {
    const GetModelMake = await ModelMake(modelId);
    listModel(GetModelMake);
  };
  useEffect(() => {
    readModel();
  }, [modelId]);

  const readVersion = async () => {
    const GetVersionModel = await VersionModel(versionId);
    listVersion(GetVersionModel);
  };
  useEffect(() => {
    readVersion();
  }, [versionId]);

  return (
    <>
      <form>
        <input></input>
        <select className="input" name="grade_group">
          <option></option>
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>

        <select
          className="input"
          name="grade_group "
          onChange={(e) => {
            setModelId(e.target.value);
          }}
        >
          <option>Todas</option>
          {GetMakeCar.map((make) => {
            return <option value={make.ID}>{make.Name}</option>;
          })}
        </select>
        <select
          className="input"
          name="grade_group"
          onChange={(e) => {
            setVersionId(e.target.value);
          }}
        >
          {model && (
            <>
              {model.map((make) => {
                return <option value={make.ID}>{make.Name}</option>;
              })}
            </>
          )}
        </select>
        <select className="input" name="grade_group">
          {version && (
            <>
              {version.map((versio) => {
                return <option value={versio.ID}>{versio.Name}</option>;
              })}
            </>
          )}
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>
      </form>
    </>
  );
}

Page.getInitialProps = async () => {
  const GetMakeCar = await MakeCar();
  return {
    GetMakeCar,
  };
};

export default Page;
