import React, { useState, useEffect } from "react";
import { ContextGlobalProvider } from "../ContextGlobal";

function Page(props) {
  const [outro, setOutro] = useState();
  var url;
  if (typeof window !== "undefined") {
    url = window.location?.href;
  }
  const urlTransform = url?.split("/")[3];
  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/posts/${urlTransform}`)
      .then((response) => response.json())
      .then((json) => setOutro(json));
  }, [urlTransform]);
  return (
    <div>
      {outro && (
        <div>
          <p>{outro.id}</p>
          <p>{outro.title}</p>
          <p>{outro.body}</p>
        </div>
      )}
    </div>
  );
}
export default Page;
