import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [name, setName] = useState();
  const [rede, setRede] = useState();
  const [culto, setCulto] = useState();
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";

  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  const post = (data) => {
    Axios.post(url, data).then((res) => {});
  };
  console.log(data, "data");
  const salve = () => {
    post({ nome: name, rede: rede, culto: culto });
  };

  return (
    <>
      <input
        onKeyUp={(e) => setName(e.currentTarget.value)}
        type="text"
        placeholder="Pesquisar por estudante"
      />
      <input
        onKeyUp={(e) => setRede(e.currentTarget.value)}
        type="text"
        placeholder="Pesquisar por estudante"
      />
      <select
        onChange={(e) => {
          setCulto(e.target.value);
        }}
      >
        <option value="">Selecione o Culto</option>
        <option value="manha">Manha</option>
        <option value="noite">Noite</option>
      </select>
      <button onClick={salve}>Salvar</button>
      <br></br>
      {data && (
        <div>
          {Object.keys(data.data).map((individuo) => {
            return (
              <div key={individuo}>
                <p>{individuo}</p>;<p>{data.data[individuo].nome}</p>;
              </div>
            );
          })}
        </div>
      )}
      {loading && <p>Loading</p>}
    </>
  );
}

export default Page;
