import React, { useState, useEffect } from "react";

import { updateParameter } from "../src/helpers/update";
import {
  MakeCar,
  ModelMake,
  VersionModel,
  VehiclesPage1,
  VehiclesPage2,
  VehiclesPage3,
} from "../src/services/requests";

function Page({
  GetMakeCar,
  GetVehiclesPage1,
  GetVehiclesPage2,
  GetVehiclesPage3,
}) {
  const [modelId, setModelId] = useState();
  const [model, listModel] = useState();
  const [versionId, setVersionId] = useState();
  const [version, listVersion] = useState();
  const readModel = async () => {
    const GetModelMake = await ModelMake(modelId);
    listModel(GetModelMake);
  };
  useEffect(() => {
    readModel();
  }, [modelId]);

  const readVersion = async () => {
    const GetVersionModel = await VersionModel(versionId);
    listVersion(GetVersionModel);
  };
  useEffect(() => {
    readVersion();
  }, [versionId]);

  const allVehicles = [
    ...GetVehiclesPage1,
    ...GetVehiclesPage2,
    ...GetVehiclesPage3,
  ];

  console.log(allVehicles, "allVehicles");
  return (
    <>
      <form>
        <input name="locale" placeholder="Onde:"></input>
        <select className="input" name="distance">
          <option placeholder="Raio:"></option>
        </select>

        <select
          className="input"
          name="make"
          onChange={(e) => {
            setModelId(e.target.value);
          }}
        >
          <option>Todas</option>
          {GetMakeCar.map((make) => {
            return (
              <option value={make.ID} key={make.ID}>
                {make.Name}
              </option>
            );
          })}
        </select>
        <select
          className="input"
          name="model"
          onChange={(e) => {
            setVersionId(e.target.value);
          }}
        >
          <option>Todas</option>
          {model && (
            <>
              {model.map((mode) => {
                return (
                  <option value={mode.ID} key={mode.ID}>
                    {mode.Name}
                  </option>
                );
              })}
            </>
          )}
        </select>
        <select className="input" name="year">
          {allVehicles.map((vehicle) => {
            return (
              <option>
                {vehicle.YearModel} / {vehicle.YearFab}
              </option>
            );
          })}
        </select>
        <select className="input" name="price">
          {allVehicles.map((vehicle) => {
            return (
              <>
                {vehicle.Price && (
                  <option value={vehicle.Price} key={vehicle.ID}>
                    {vehicle.Price}
                  </option>
                )}
              </>
            );
          })}
        </select>
        <select className="input" name="version">
          <option>Todas</option>
          {version && (
            <>
              {version.map((versio) => {
                return (
                  <option value={versio.ID} key={versio.ID}>
                    {versio.Name}
                  </option>
                );
              })}
            </>
          )}
        </select>
        <button>Limpar Filtros</button>
        <button>Ver Ofertas</button>
      </form>
      <div className="boxes">
        {allVehicles.map((vehicle) => {
          return (
            <div className="box">
              <p>Marca: {vehicle.Make}</p>
              <p>Modelo: {vehicle.Model}</p>
              <p>Versão: {vehicle.Version}</p>
              <img src={vehicle.Image} />
              <p>KM: {vehicle.KM}</p>
              <p>Preço: {vehicle.Price}</p>
              <p>
                Ano: {vehicle.YearModel}/ {vehicle.YearFab}
              </p>
              <p>Cor: {vehicle.Color}</p>
            </div>
          );
        })}
      </div>
    </>
  );
}

Page.getInitialProps = async () => {
  const GetMakeCar = await MakeCar();
  const GetVehiclesPage1 = await VehiclesPage1();
  const GetVehiclesPage2 = await VehiclesPage2();
  const GetVehiclesPage3 = await VehiclesPage3();
  return {
    GetMakeCar,
    GetVehiclesPage1,
    GetVehiclesPage2,
    GetVehiclesPage3,
  };
};

export default Page;
