function Page({ GetMakeCar, GetModelMake, filtrar, query }) {
  const [selectSelecionado, setSelectSelecionado] = useState();
  console.log(selectSelecionado, "selectSelecionado");
  function teste(e) {
    useEffect(() => {
      setSelectSelecionado(e.target.value);
    }, [selectSelecionado]);
  }
  return (
    <>
      <form>
        <input></input>
        <select className="input" name="grade_group">
          <option></option>
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>
        {/* <select
          defaultValue={tableSelect}
          className="input"
          onChange={(e) => {
            const value = e.currentTarget.value;
            const url = updateParameter(window.location.href, "table", value);
            window.location = url;
          }}
        ></select> */}
        {/* <Select
          className="releases__select"
          label="Disciplina:"
          options={GetMakeCar}
          onChange={(e) => {
            e.target.value;
          }}
        ></Select> */}
        <select
          className="input"
          name="grade_group "
          onChange={(e) => {
            teste(e);
          }}
        >
          <option>Todas</option>
          {GetMakeCar.map((make) => {
            return <option value={make.ID}>{make.Name}</option>;
          })}
        </select>
        <select className="input" name="grade_group">
          {GetModelMake.map((make) => {
            return <option value={make.ID}>{make.Name}</option>;
          })}
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>
      </form>
    </>
  );
}

Page.getInitialProps = async ({ query, selectSelecionado }) => {
  let filtrar = [];
  console.log(selectSelecionado, "selectSelecionado");
  const GetMakeCar = await MakeCar();
  let GetModelMake = [];

  if (selectSelecionado) {
    GetModelMake = await ModelMake(selectSelecionado);
  }
  // const GetVersionModel = await VersionModel();
  // const GetVehicles = await Vehicles();
  return {
    filtrar,
    query,
    GetMakeCar,
    GetModelMake,
  };
};

export default Page;
