import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";

  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  const post = (data) => {
    Axios.post(url, data).then((res) => {
      console.log(res.data);
    });
  };

  const salve = () => {
    post({ nome: "Felipe Paulino", rede: "radicais", culto: "noite" });
  };

  const [name, setName] = useState();
  const [rede, setRede] = useState();
  const [culto, setCulto] = useState();

  return (
    <form>
      <input
        onKeyUp={(e) => setName(e.currentTarget.value)}
        type="text"
        placeholder="Pesquisar por estudante"
      />
      <input
        onKeyUp={(e) => setRede(e.currentTarget.value)}
        type="text"
        placeholder="Pesquisar por estudante"
      />
      <select
        onChange={(e) => {
          e.target.value;
        }}
      >
        <option value="manha">Manha</option>
        <option value="noite">Noite</option>
      </select>
      {JSON.stringify(data)}

      <button onClick={salve}>Salvar</button>
      {loading && <p>Loading</p>}
    </form>
  );
}

export default Page;
