import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [name, setName] = useState();
  const [rede, setRede] = useState();
  const [culto, setCulto] = useState();
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";

  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  return (
    <>
      {data && (
        <>
          <select
            onChange={(e) => {
              setRede(e.target.value);
            }}
            required
          >
            <option value="">Todos</option>
            <option value="manoel">Pr Manoel - Ebenezér</option>
            <option value="gilberto">Pr Gilberto - Kadosh</option>
            <option value="lincoln">Pr Lincoln - Shekinah</option>
            <option value="reginaldo">Pr Reginaldo - Radicais</option>
            <option value="darliene">Pr Darliene - Kids </option>
          </select>
          <h1>Sabado</h1>
          <table>
            <thead>
              <tr>
                <th>Nome</th>
                <th>Rede</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(data).map((individuo) => {
                if (data[individuo].culto == "sabado") {
                  return (
                    <tr key={individuo}>
                      <td>
                        {data[individuo].nome} {data[individuo].sobrenome}
                      </td>
                      <td>Pr {data[individuo].rede}</td>
                    </tr>
                  );
                }
              })}
            </tbody>
          </table>
          <h1>Manhã</h1>
          <table>
            <thead>
              <tr>
                <th>Nome</th>
                <th>Rede</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(data).map((individuo) => {
                if (data[individuo].culto == "manha") {
                  return (
                    <tr key={individuo}>
                      <td>
                        {data[individuo].nome} {data[individuo].sobrenome}
                      </td>
                      <td>{data[individuo].rede}</td>
                    </tr>
                  );
                }
              })}
            </tbody>
          </table>
          <h1>Noite</h1>
          <table>
            <thead>
              <tr>
                <th>Nome</th>
                <th>Rede</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(data).map((individuo) => {
                if (data[individuo].culto == "noite") {
                  return (
                    <tr key={individuo}>
                      <td>
                        {data[individuo].nome} {data[individuo].sobrenome}
                      </td>
                      <td>{data[individuo].rede}</td>
                    </tr>
                  );
                }
              })}
            </tbody>
          </table>
        </>
      )}
      {loading && <p>Loading</p>}
    </>
  );
}

export default Page;
