import Head from "next/head";
import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";

function Page() {
  const [registros, setRegistros] = useState();
  const [apagarRegistros, setApagarRegistros] = useState(false);
  const [filtrarRegistros, setFiltraRegistros] = useState(false);
  console.log(registros);
  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((json) => setRegistros(json));
  }, []);

  function remove() {
    setApagarRegistros(true);
    fetch("https://jsonplaceholder.typicode.com/posts/1", {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((json) => setRegistros(json))
      .then((json) => setApagarRegistros(false));
  }

  function filtrar(value) {
    if (value >= 1) {
      setFiltraRegistros(true);
      fetch(`https://jsonplaceholder.typicode.com/posts?userId=${value}`)
        .then((response) => response.json())
        .then((json) => setRegistros(json))
        .then((json) => setFiltraRegistros(json));
    }
  }

  return (
    <>
      <Head>
        <title>Seja bem vindo</title>
      </Head>
      <div className="container">
        <header className="header">
          <img className="logo" src="/images/logo.png" alt="petz" />
          {apagarRegistros && <p>Apagando...</p>}
          {filtrarRegistros && <p>Filtrando...</p>}
          <div className="filtros">
            <select
              onChange={(e) => {
                filtrar(e.target.value);
              }}
            >
              <option>Selecione</option>
              <option value="1"> 1 ao 10</option>
              <option value="2"> 11 ao 20</option>
            </select>
            <button
              onClick={() => {
                remove();
              }}
            >
              Apagar todos os registros
            </button>
          </div>
        </header>
        <>
          {registros?.length >= 1 && (
            <div>
              {registros.map((registro) => {
                return (
                  <>
                    <span>{registro.id}</span>
                    <span>{registro.title}</span>
                    <a href={`${registro.id}`}>Visualizar</a>
                    <br />
                  </>
                );
              })}
            </div>
          )}
        </>
      </div>
    </>
  );
}

export default Page;
