import React from "react";
import Axios from "axios";
import Quadrado from "../../public/images/carraca.svg";

export default function Form({ titulo, culto, orderListagem, remocao }) {
  const doRemove = (id) => {
    remove(`https://aguaviva-rp.firebaseio.com/teste/${id}.json`);
  };
  const remove = (url) => {
    Axios.delete(url).then(() => {
      alert(`você deletou ${url}`);
      window.location = "listagem";
    });
  };
  const doEdit = (id, data) => {
    if (data.chegada) {
      edit(`https://aguaviva-rp.firebaseio.com/teste/${id}.json`, {
        ...data,
        chegada: false,
      });
    } else {
      edit(`https://aguaviva-rp.firebaseio.com/teste/${id}.json`, {
        ...data,
        chegada: true,
      });
    }
  };
  const edit = (url, data) => {
    Axios.put(url, data).then(() => {
      alert(`você editou ${url}`);
      window.location = "listagem";
    });
  };
  return (
    <>
      <h1>{titulo}</h1>
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Rede</th>

            <th>Chegada</th>
            {remocao && <th>Status</th>}
          </tr>
        </thead>
        <tbody>
          {orderListagem && (
            <>
              {orderListagem.map((individuo) => {
                return (
                  <>
                    {individuo.map((ind) => {
                      if (ind.culto == culto) {
                        return (
                          <tr key={ind} className={individuo[0]}>
                            <td>
                              {ind.nome} {ind.sobrenome}
                            </td>
                            <td>Pr {ind.rede}</td>

                            <td onClick={(e) => doEdit(individuo[0], ind)}>
                              {ind.chegada ? "chegou" : "não chegou"}
                            </td>
                            {remocao && (
                              <td>
                                <button
                                  className="remove"
                                  onClick={(e) => doRemove(individuo[0])}
                                >
                                  Remover
                                </button>
                              </td>
                            )}
                          </tr>
                        );
                      }
                    })}
                  </>
                );
              })}
            </>
          )}
        </tbody>
      </table>
    </>
  );
}
