import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [nome, setNome] = useState();
  const [rede, setRede] = useState();
  const [sobrenome, setSobrenome] = useState();
  const [culto, setCulto] = useState();
  const url = "https://aguaviva-rp.firebaseio.com/inscricoes.json";
  var date = new Date();
  var dia_sem = date.getDay();
  const [qtdSabado, setQtdSabado] = useState();
  var qtdMAxima = 60;
  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  useEffect(() => {
    if (data) {
      const cultoSabado = Object.keys(data).filter((pessoa) => {
        return data[pessoa].culto == "sabado";
      });
      setQtdSabado(cultoSabado.length);
    }
  }, [data]);
  console.log(qtdSabado, "qtdSabado");
  const post = (data) => {
    Axios.post(url, data).then((res) => {
      console.log(data, "data");
      alert("você confirmou sua presença no culto");
      window.location = "/listagem";
    });
  };

  const salve = () => {
    post({ nome: nome, sobrenome: sobrenome, rede: rede, culto: culto });
  };

  return (
    <div className="container">
      {(dia_sem == 0 || dia_sem == 5 || dia_sem == 6) && (
        <>
          <div className="header">
            <img src="/images/logo.jpg" />
            <h1>Formulário de preenchimento para o culto</h1>
          </div>
          <div className="form">
            <label>Nome :</label>
            <input
              onKeyUp={(e) => setNome(e.currentTarget.value)}
              type="text"
              placeholder="Nome "
              required
            />
            <br></br>
            <label>Sobrenome:</label>
            <input
              onKeyUp={(e) => setSobrenome(e.currentTarget.value)}
              type="text"
              placeholder="Sobrenome"
              required
            />
            <br></br>
            <label>Rede</label>
            <select
              onChange={(e) => {
                setRede(e.target.value);
              }}
              required
            >
              <option value="">Selecione sua rede</option>
              <option value="manoel">Pr Manoel - Ebenezér</option>
              <option value="gilberto">Pr Gilberto - Kadosh</option>
              <option value="lincoln">Pr Lincoln - Shekinah</option>
              <option value="reginaldo">Pr Reginaldo - Radicais</option>
              <option value="darliene">Pr Darliene - Kids </option>
            </select>
            <br></br>
            <label>Qual culto deseja ir?</label>
            <select
              onChange={(e) => {
                setCulto(e.target.value);
              }}
              required
            >
              <option value="">Selecione o culto</option>
              <option value="sabado">Sábado - 19hs</option>
              <option value="manha">Domingo - 9hs</option>
              <option value="noite">Domingo - 18hs </option>
            </select>
            <span>Sábado 19hs - restam {qtdMAxima - qtdSabado} Vagas </span>
            <span>Domingo 9hs </span>
            <span>Domingo 18hs </span>

            <br></br>
            <button onClick={salve}>Inscrever</button>
          </div>
        </>
      )}
      {dia_sem >= 1 && dia_sem <= 4 && (
        <div>
          <p>o site só abrirá as inscrições de sexta a domingo</p>
        </div>
      )}
    </div>
  );
}

export default Page;
