import request from "./request";

export function Posts() {
  return request(`https://jsonplaceholder.typicode.com/posts`)
    .then((res) => res.json())
    .catch((err) => {
      throw err;
    });
}
export function Filtrar(filtro) {
  return request(`https://jsonplaceholder.typicode.com/posts?userId=${filtro}`)
    .then((res) => res.json())
    .catch((err) => {
      throw err;
    });
}
export function Remover(body) {
  return request(`http://jsonplaceholder.typicode.com/posts/1`, {
    method: "DELETE",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(body),
  })
    .then((res) => res.json())
    .catch((err) => {
      throw err;
    });
}
