    teste();
  }, [selectSelecionado]);

  console.log(ativador, "ativador");
  return (
    <>
      <form>
        <input></input>
        <select className="input" name="grade_group">
          <option></option>
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>
        {/* <select
          defaultValue={tableSelect}
          className="input"
