            window.location = url;
          }}
        ></select> */}
        {/* <Select
          className="releases__select"
          label="Disciplina:"
          options={GetMakeCar}
          onChange={(e) => {
            e.target.value;
          }}
        ></Select> */}
        <select
          className="input"
          name="grade_group "
          onChange={(e) => {
            setSelectSelecionado(e.target.value);
          }}
        >
          <option>Todas</option>
          {GetMakeCar.map((make) => {
            return <option value={make.ID}>{make.Name}</option>;
          })}
        </select>
        <select className="input" name="grade_group">
          {GetModelMake.map((make) => {
            return <option value={make.ID}>{make.Name}</option>;
          })}
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>
      </form>
    </>
  );
}

Page.getInitialProps = async ({ query, selectSelecionado }) => {
  let filtrar = [];
  const GetMakeCar = await MakeCar();
  // let GetModelMake = [];
  // console.log(selectSelecionado, "selectSelecionado");
  // if (selectSelecionado) {
  //   GetModelMake = await ModelMake(2);
  // }
  // const GetVersionModel = await VersionModel();
  // const GetVehicles = await Vehicles();
  return {
    filtrar,
    query,
    GetMakeCar,
  };
};

export default Page;
